package com.example.monitoringlistrik3phase.helperimport android.content.Contextimport android.content.SharedPreferencesclass SharedPreference(context: Context) {    companion object {        private const val SP_NAME = "com.example.monitoringlistrik3phase.shared.pref"        private const val TOKEN_FIREBASE = "token_firebase"        private const val SUBSCRIBE_TOPIC = "subscribe_topic"    }    private val preference: SharedPreferences =        context.getSharedPreferences(SP_NAME, Context.MODE_PRIVATE)    private val editor: SharedPreferences.Editor = preference.edit()    fun saveTokenFirebase(tokenFirebase: String) {        editor.putString(TOKEN_FIREBASE, tokenFirebase)        editor.apply()    }    fun saveStateSubscribe(isSubscribe: Boolean) {        editor.putBoolean(SUBSCRIBE_TOPIC, isSubscribe)        editor.apply()    }    fun getTokenFirebase(): String {        return preference.getString(TOKEN_FIREBASE, "").toString()    }    fun getStateSubscribe(): Boolean {        return preference.getBoolean(SUBSCRIBE_TOPIC, false)    }    fun clearPreference() {        editor.remove(TOKEN_FIREBASE)        editor.remove(TOKEN_FIREBASE)        editor.apply()    }}