package com.example.monitoringlistrik3phase.ui.historyimport android.view.LayoutInflaterimport android.view.ViewGroupimport androidx.recyclerview.widget.RecyclerViewimport com.example.monitoringlistrik3phase.databinding.ItemViewRiwayatBindingclass HistoryAdapter(private val listData: ArrayList<HistoryModel>) :    RecyclerView.Adapter<HistoryAdapter.HistoryViewHolder>() {    inner class HistoryViewHolder(private val binding: ItemViewRiwayatBinding) :        RecyclerView.ViewHolder(binding.root) {        fun bind(historyModel: HistoryModel) {            binding.apply {                historyModel.apply {                    tvDate.text = date                    tvKeteranganFasa1.text = fasa1                    tvKeteranganFasa2.text = fasa2                    tvKeteranganFasa3.text = fasa3                    tvPersentase1.text = persentase1                    tvPersentase2.text = persentase2                    tvPersentase3.text = persentase3                }            }        }    }    override fun onCreateViewHolder(        parent: ViewGroup,        viewType: Int,    ): HistoryAdapter.HistoryViewHolder {        return HistoryViewHolder(ItemViewRiwayatBinding.inflate(LayoutInflater.from(parent.context),            parent,            false))    }    override fun onBindViewHolder(holder: HistoryAdapter.HistoryViewHolder, position: Int) {        holder.bind(listData[position])    }    override fun getItemCount(): Int = listData.size}