package com.example.monitoringlistrik3phase.ui.setting.notifikasiimport android.annotation.SuppressLintimport android.view.LayoutInflaterimport android.view.ViewGroupimport androidx.recyclerview.widget.RecyclerViewimport com.example.monitoringlistrik3phase.databinding.ItemViewMessageBinding@SuppressLint("NotifyDataSetChanged")class MessageAdapter() :    RecyclerView.Adapter<MessageAdapter.MessageViewHolder>() {    private val listData = ArrayList<ItemMessage>()    fun clear(){        listData.clear()        notifyDataSetChanged()    }    fun addAll(listNew: ArrayList<ItemMessage>){        listData.addAll(listNew)        notifyDataSetChanged()    }    inner class MessageViewHolder(private val binding: ItemViewMessageBinding) :        RecyclerView.ViewHolder(binding.root) {        fun bind(message: ItemMessage) {            binding.apply {                tvTitleMessage.text = message.title                tvContentMessage.text = message.body                tvTime.text = message.time            }        }    }    override fun onCreateViewHolder(        parent: ViewGroup,        viewType: Int,    ): MessageAdapter.MessageViewHolder {        return MessageViewHolder(ItemViewMessageBinding.inflate(LayoutInflater.from(parent.context),            parent,            false))    }    override fun onBindViewHolder(holder: MessageAdapter.MessageViewHolder, position: Int) {        holder.bind(listData[position])    }    override fun getItemCount(): Int = listData.size}